mapper.select:
select `permission_id`,`system_id`,`pid`,`name`,`type`,`permission_value`,`uri`,`icon`,`status`,`ctime`,`orders` from upms_permission

  		   and permission_id = #{permissionId} 
  		   and system_id = #{systemId} 
  		   and pid = #{pid} 
  		   and name = #{name} 
  		   and type = #{type} 
  		   and permission_value = #{permissionValue} 
  		   and uri = #{uri} 
  		   and icon = #{icon} 
  		   and status = #{status} 
  		   and ctime = #{ctime} 
  		   and orders = #{orders} 

order by orders asc, permission_id asc

	limit #{offset}, #{limit}

select count(*) from upms_permission

  		   and permission_id = #{permissionId} 
  		   and system_id = #{systemId} 
  		   and pid = #{pid} 
  		   and name = #{name} 
  		   and type = #{type} 
  		   and permission_value = #{permissionValue} 
  		   and uri = #{uri} 
  		   and icon = #{icon} 
  		   and status = #{status} 
  		   and ctime = #{ctime} 
  		   and orders = #{orders}
